// Parse AI response and create search array
const aiResponse = $input.first().json;

// Get the text from the array structure
const enhancedTerms = aiResponse.text || '';

// Parse the terms
const termsArray = enhancedTerms
  .split(',')
  .map(term => term.trim())
  .filter(term => term.length > 0);

// Ensure we have at least the original term
if (termsArray.length === 0) {
  termsArray.push($('Search Form').item.json['Business Type']);
}

return {
  searchTerms: termsArray,
  location: $('Search Form').item.json['Location (City, State)'],
  email: $('Search Form').item.json['Your Email'],
  timestamp: new Date().toISOString()
};
